window.onload = function() {
  var e = React.createElement;
  var hours = new Date().getHours();
  var morning = new Array(12);
  var afternoon = new Array(17);
  var greeting = "Good evening";
  
  if (hours < morning.length) {
    greeting = "Good morning"
  }
  else if (hours < afternoon.length) {
    greeting = "Hello there"
  } 
  
  var intro = e( 
    "span", 
    { className: "greeting" },
    greeting
  );

  ReactDOM.render(
    intro,
    document.getElementById('pdtr-app')
  );
};
